import React from 'react';
import './Navigation.css';

class Navigation extends React.Component {
    render(){
        const section = ['Home', 'About', 'Project', 'Contact'];
        //function map will loop through the array and return the values based on the condition
        const navLinks = section.map( section => {
            return (
                <li><a href={"#" + section}>{section}</a></li>

            )

        })
        return (
            <nav>
               <h2 className="Logo">{this.props.LogoTitle}</h2>

               <ul>
                   {navLinks}
               </ul>
            </nav>

        );

    }

}


//Components to add a counter to my graduation: 


//Calculate how much time is left: 
const calculateTimeLeft = () => {
    //The method will grab the current year: 
    let current_year = new Date().getFullYear();
    //The difference between the current date and the day of my graduation
    //The + before the new Date object is shorthand to tell JavaScript to cast the object as an integer,
    // which gives you the object’s Unix timestamp represented as microseconds since the epoch.
    const difference = +new Date(`${year}-10-1`) - +new Date();

    let timeLeft = {};
    if(difference > 0){
        timeLeft = {
            days: Math.floor(difference/(1000 * 60 * 60 * 24)),
            hours: Math.floor(difference/(1000 * 60 * 60)) % 24,
            minutes: Math.floor((difference / 1000 / 60) % 60),
            seconds: Math.floor((difference / 1000) % 60)
        }
    }

    return timeLeft;

}

export default Navigation;
